mapper.select:
  	SELECT
  		bbd.PID,
		bbd.borrow_id,
		bbd.customer_id,
		bbd.invest_award_id,
		bbd.vipinfo_id,
		bbd.investment_time,
		bbd.interest_time,
		bbd.investment_amount,
		bbd.award_amount,
		bb.borrow_code,
		bb.borrow_name,
		(bb.borrow_rate * 100) borrow_rate,
		bb.bor_deadline,
		bb.bor_status,
		cc.customer_name,
		cc.sname,
		pia.invest_award_value,
		(
			(
				ifnull(pia.invest_award_value/100, 0) + ifnull(bb.borrow_rate,0)
			) * ifnull(bb.bor_deadline, 0) * ifnull(bbd.investment_amount, 0)/100
		) interest
	FROM
		t_biz_borrow_detail bbd
	LEFT JOIN t_biz_borrow bb ON bbd.borrow_id = bb.PID
	LEFT JOIN t_cust_customer cc ON bbd.customer_id = cc.PID
	LEFT JOIN t_pub_invest_award pia ON bbd.invest_award_id = pia.PID
	LEFT JOIN t_pub_vipinfo pv ON bbd.vipinfo_id = pv.PID
	WHERE 1=1


		 AND cc.customer_name like concat('%',#{customer.customerName,jdbcType=VARCHAR},'%')


		 AND cc.sname like concat('%',#{customer.sname,jdbcType=VARCHAR},'%')


		 AND cc.phone_no like concat('%',#{customer.phoneNo,jdbcType=VARCHAR},'%')


		 AND bbd.investment_time  >=  #{beginInvestmentTime,jdbcType=DATE}


		 AND bbd.investment_time  <  date_add(#{endInvestmentTime,jdbcType=DATE}, INTERVAL 1 day)


		 AND bbd.interest_time  >=  #{beginInterestTime,jdbcType=DATE}


		 AND bbd.interest_time  <  date_add(#{endInterestTime,jdbcType=DATE}, INTERVAL 1 day)


		 AND bb.bor_status = #{bizBorrow.borStatus,jdbcType=VARCHAR}


		 AND bb.borrow_code = #{bizBorrow.borrowCode,jdbcType=VARCHAR}


		 AND bb.borrow_name = #{bizBorrow.borrowName,jdbcType=VARCHAR}


		 AND bb.borrow_type = #{bizBorrow.borrowType,jdbcType=VARCHAR}


		 AND bbd.investment_amount >=  #{minInvestmentAmount,jdbcType=VARCHAR}


		 AND bbd.investment_amount  <=  #{maxInvestmentAmount,jdbcType=VARCHAR}


		 AND bb.borrow_rate  >=  #{bizBorrow.borrowRateMin,jdbcType=VARCHAR}


		 AND bb.borrow_rate  <=  #{bizBorrow.borrowRateMax,jdbcType=VARCHAR}


		 AND bb.bor_deadline  >=  CONVERT(#{bizBorrow.borDeadlineMin,jdbcType=VARCHAR},SIGNED)


		 AND bb.bor_deadline  <=  CONVERT(#{bizBorrow.borDeadlineMax,jdbcType=VARCHAR},SIGNED)


		 AND bb.repayment_type = #{bizBorrow.repaymentType,jdbcType=VARCHAR}


		 AND bb.accrual_type = #{bizBorrow.accrualType,jdbcType=VARCHAR}

	LIMIT #{page} , #{limit}

  	SELECT
  		COUNT(1)
	FROM
		t_biz_borrow_detail bbd
	LEFT JOIN t_biz_borrow bb ON bbd.borrow_id = bb.PID
	LEFT JOIN t_cust_customer cc ON bbd.customer_id = cc.PID
	LEFT JOIN t_pub_invest_award pia ON bbd.invest_award_id = pia.PID
	LEFT JOIN t_pub_vipinfo pv ON bbd.vipinfo_id = pv.PID
	WHERE 1=1


		 AND cc.customer_name like concat('%',#{customer.customerName,jdbcType=VARCHAR},'%')


		 AND cc.sname like concat('%',#{customer.sname,jdbcType=VARCHAR},'%')


		 AND cc.phone_no like concat('%',#{customer.phoneNo,jdbcType=VARCHAR},'%')


		 AND bbd.investment_time  >=  #{beginInvestmentTime,jdbcType=DATE}


		 AND bbd.investment_time  <  date_add(#{endInvestmentTime,jdbcType=DATE}, INTERVAL 1 day)


		 AND bbd.interest_time  >=  #{beginInterestTime,jdbcType=DATE}


		 AND bbd.interest_time  < date_add(#{endInterestTime,jdbcType=DATE}, INTERVAL 1 day)


		 AND bb.bor_status = #{bizBorrow.borStatus,jdbcType=VARCHAR}


		 AND bb.borrow_code = #{bizBorrow.borrowCode,jdbcType=VARCHAR}


		 AND bb.borrow_name = #{bizBorrow.borrowName,jdbcType=VARCHAR}


		 AND bb.borrow_type = #{bizBorrow.borrowType,jdbcType=VARCHAR}


		 AND bbd.investment_amount >=  #{minInvestmentAmount,jdbcType=VARCHAR}


		 AND bbd.investment_amount  <=  #{maxInvestmentAmount,jdbcType=VARCHAR}


		 AND bb.borrow_rate  >=  #{bizBorrow.borrowRateMin,jdbcType=VARCHAR}


		 AND bb.borrow_rate  <=  #{bizBorrow.borrowRateMax,jdbcType=VARCHAR}


		 AND bb.bor_deadline  >=  #{bizBorrow.borDeadlineMin,jdbcType=VARCHAR}


		 AND bb.bor_deadline  <=  #{bizBorrow.borDeadlineMax,jdbcType=VARCHAR}


		 AND bb.repayment_type = #{bizBorrow.repaymentType,jdbcType=VARCHAR}


		 AND bb.accrual_type = #{bizBorrow.accrualType,jdbcType=VARCHAR}

select 

from t_biz_borrow_detail
where PID = #{pid,jdbcType=VARCHAR}

select
from t_biz_borrow_detail detail LEFT JOIN t_cust_customer cus ON detail.customer_id = cus.PID LEFT JOIN t_biz_borrow borrow ON detail.borrow_id = borrow.PID
and t.PID = #{map.pid,jdbcType=VARCHAR}

select d.PID, d.borrow_id, d.customer_id, d.investment_amount, d.invest_award_id, d.investment_time, d.interest_time, d.investment_type, FUNC_GET_DICT_CONT_NAME('INVEST_STYLE',investment_type) AS investment_type_name, d.vipinfo_id, d.manager_rate, d.award_amount, d.status, d.create_user, d.create_time, d.last_update_user, d.last_update_time, d.bor_det_desc, d.investment_payoffs,cc.sname,cc.phone_no,IFNULL(cc.customer_name,cc.phone_no) as customer_name from t_biz_borrow_detail d LEFT JOIN t_cust_customer cc ON d.customer_id = cc.PID where 1=1 and d.status = 1
and d.borrow_id = #{borrowId,jdbcType=VARCHAR}
order by create_time desc
limit #{pageIndex},#{pageSize}

select customer_id from t_biz_borrow_detail where 1=1
borrow_id = #{borrowId,jdbcType=VARCHAR}
#{conditionValue,jdbcType=VARCHAR}
GROUP BY customer_id
