mapper.select:
    SELECT 

	FROM
		t_biz_receipt_transfer tb
	LEFT JOIN t_biz_borrow bb ON tb.borrow_id = bb.PID
	WHERE
		(
			tb.`status` = 1
			OR tb.`status` = 2
		)
		AND tb.version   >=  0


				AND tb.success_amount   >=   #{condition.startSuccessAmount,jdbcType=VARCHAR} 


				AND tb.success_amount   <   #{condition.endSuccessAmount,jdbcType=VARCHAR} 


				AND tb.time_remaining   >=   #{condition.startTimeRemaining,jdbcType=VARCHAR} 


				AND tb.time_remaining   <   #{condition.endTimeRemaining,jdbcType=VARCHAR} 


	ORDER BY
		tb.`status` ASC,
		tb.create_time DESC
	LIMIT #{pageIndex,jdbcType=INTEGER} ,#{pageSize,jdbcType=INTEGER}

    SELECT 
   		COUNT(tb.PID )
	FROM
		t_biz_receipt_transfer tb
	LEFT JOIN t_biz_borrow bb ON tb.borrow_id = bb.PID
	WHERE
		(
			tb.`status` = 1
			OR tb.`status` = 2
		)
		AND tb.version   >=  0


				AND tb.success_amount   >=   #{condition.startSuccessAmount,jdbcType=VARCHAR} 


				AND tb.success_amount   <   #{condition.endSuccessAmount,jdbcType=VARCHAR} 


				AND tb.time_remaining   >=   #{condition.startTimeRemaining,jdbcType=VARCHAR} 


				AND tb.time_remaining   <   #{condition.endTimeRemaining,jdbcType=VARCHAR}

SELECT
FROM t_biz_receipt_transfer brt LEFT JOIN t_cust_customer cc ON brt.create_user = cc.PID LEFT JOIN t_biz_borrow bb ON bb.PID = brt.borrow_id LEFT JOIN t_biz_receipt_plan brp ON brp.transfer_id = brt.PID WHERE 1 = 1
ORDER BY brp.expire_time ASC LIMIT #{start,jdbcType=VARCHAR},#{end,jdbcType=VARCHAR}
