mapper.select:
select 

from t_biz_repayment_plan
where receipt_paln_status = #{bizReplanStatus,jdbcType=VARCHAR}
 and DATE_FORMAT(expire_time,'%Y-%m-%d')  <=  #{time,jdbcType=VARCHAR}

select 

from t_biz_repayment_plan
where receipt_paln_status = #{bizReplanStatus,jdbcType=VARCHAR}
 and DATE_FORMAT(repaid_time,'%Y-%m-%d')  <  #{time,jdbcType=VARCHAR}

SELECT count(1) as overdue_num, sum(overdueAmt) as overdue_amt FROM ( SELECT (IFNULL(capital,0) + IFNULL(interest,0)) AS overdueAmt, datediff( IFNULL(repaid_time,#{toDay,jdbcType=TIMESTAMP}), expire_time ) overdue_days FROM t_biz_repayment_plan where status = 1 ) t WHERE 1 = 1
and t.overdue_days >= #{startOverdueDay,jdbcType=VARCHAR}
AND t.overdue_days <= #{endOverdueDay,jdbcType=VARCHAR}

	select 

    from t_biz_repayment_plan
    where status = 1
     and borrow_id = #{borrowId,jdbcType=VARCHAR}
     ORDER BY planindex;
