mapper.select:
select cc.*, f.PID as file_pid , f.file_name, f.file_url, TEMP.dict_cont_name from t_cont_column_content cc LEFT JOIN (SELECT t.colu_cont_id, group_concat(c.dict_cont_name ) as dict_cont_name, group_concat(c.PID) as dictId FROM t_cont_tag t inner join t_sys_dictionary_content c where t.dict_cont_id = c.PID GROUP BY t.colu_cont_id) TEMP ON cc.pid = TEMP.colu_cont_id LEFT JOIN t_cont_colu_content_file_rel rel ON rel.colu_cont_id = cc.pid LEFT JOIN t_pub_file f ON f.pid = rel.file_id where 1=1
and cc.title_name like concat('%',#{map.titleName,jdbcType=VARCHAR},'%' )
and cc.status =#{map.status,jdbcType=VARCHAR}
and cc.create_user like concat('%',#{map.createUser,jdbcType=VARCHAR},'%' )
and cc.create_time = #{map.createTime,jdbcType=TIMESTAMP}
and cc.last_update_user like concat('%',#{map.lastUpdateUser,jdbcType=VARCHAR},'%' )
and cc.parent_id like #{map.parentId,jdbcType=VARCHAR}
and cc.last_update_time like #{map.lastUpdateTime,jdbcType=TIMESTAMP}
and cc.version = #{map.version,jdbcType=VARCHAR}
and cc.pid like = #{map.pid,jdbcType=VARCHAR}
and cc.colu_id = #{map.coluId,jdbcType=VARCHAR}
and dictId like concat('%',#{map.dictContName,jdbcType=VARCHAR},'%' )
and DATE_FORMAT(cc.last_update_time ,'%Y-%m-%d') >= #{map.lastBeginUpdateTime,jdbcType=VARCHAR}
and DATE_FORMAT(cc.last_update_time ,'%Y-%m-%d') <= #{map.lastEndUpdateTime,jdbcType=VARCHAR}
and DATE_FORMAT(cc.create_time ,'%Y-%m-%d') >= #{map.createBeginTime,jdbcType=VARCHAR}
and DATE_FORMAT(cc.create_time ,'%Y-%m-%d') <= #{map.createEndTime,jdbcType=VARCHAR}
order by cc.colu_order desc

mapper.update:
update t_cont_column_content
set status = #{map.status,jdbcType=VARCHAR}
where
PID in

	#{pid}

update t_cont_column_content
set is_reading = #{map.isReading,jdbcType=VARCHAR}
where
PID in

	#{pid}

mapper.select:
select cc.*,f.PID AS file_pid,f.file_name,f.file_url
,(SELECT COUNT(*) FROM t_sys_notice_read_record r where r.column_content_id = cc.pid and r.customer_id = #{customerId,jdbcType=VARCHAR}) as is_reading
from t_cont_column_content cc LEFT JOIN t_cont_colu_content_file_rel rel ON rel.colu_cont_id = cc.pid LEFT JOIN t_pub_file f ON f.pid = rel.file_id where cc.parent_id = ( select pid from t_cont_column_content where web_tag = #{webTag,jdbcType=VARCHAR})
and cc.begin_time <= NOW() AND cc.end_time >= NOW()
order by cc.colu_order desc
limit #{pageIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
