select cfw.PID, cfw.happen_time happen_time, cfw.account_balance account_balance, CASE business_type WHEN 1 THEN '账户总额' WHEN 2 THEN '投资总额' WHEN 3 THEN '投标奖励' WHEN 4 THEN '推荐奖励' WHEN 5 THEN '净赚利息' WHEN 6 THEN '充值总额' WHEN 7 THEN '提现总额' END business_type, IF(cfw.fund_type ='1', cfw.fund_value, 0) AS fund_value, IF(cfw.fund_type ='2', cfw.fund_value, 0) AS ex_penditure, cfw.fun_wat_desc fun_wat_desc from t_cust_fund_water cfw,t_cust_customer tcc where 1=1 and cfw.customer_id = tcc.PID and cfw.business_type= #{custFundWater.flag,jdbcType=VARCHAR} and cfw.customer_id = #{custFundWater.customerId,jdbcType=VARCHAR}
and cfw.happen_time >= #{custFundWater.happenBeginTime,jdbcType=DATE}
and cfw.happen_time <= #{custFundWater.happenEndTime,jdbcType=DATE}

     select sum(IF(cfw.fund_type ='1',cfw.fund_value, 0)) AS "fundValue",
			sum(IF(cfw.fund_type ='2',cfw.fund_value, 0))  AS "exPenditure"
	  from  t_cust_fund_water cfw,t_cust_customer tcc
	  where 1=1
	  and   cfw.customer_id = tcc.PID
	  and   cfw.business_type= #{custFundWater.flag,jdbcType=VARCHAR}
	  and   cfw.customer_id = #{custFundWater.customerId,jdbcType=VARCHAR}

        	and  cfw.happen_time  >=  #{custFundWater.happenBeginTime,jdbcType=DATE}


        	and  cfw.happen_time  <=  #{custFundWater.happenEndTime,jdbcType=DATE}

select cfw.PID, cfw.happen_time happen_time, cfw.account_balance account_balance, (SELECT ts.dict_cont_name FROM t_sys_dictionary tsd,t_sys_dictionary_content ts WHERE tsd.PID = ts.dict_id AND tsd.dict_code = 'CUST_TRADE_TYPE' AND ts.dict_cont_code = cfw.business_type) business_type, cfw.business_type business_type_value, cfw.fund_value fund_value, cfw.fun_wat_desc fun_wat_desc from t_cust_fund_water cfw, t_cust_customer tcc WHERE cfw.customer_id = tcc.PID AND cfw.customer_id = #{custFundWater.customerId,jdbcType=VARCHAR}
and cfw.happen_time >= #{custFundWater.happenBeginTime,jdbcType=TIMESTAMP}
and cfw.happen_time <= #{custFundWater.happenEndTime,jdbcType=TIMESTAMP}
and cfw.business_type ='302'
and cfw.business_type in ('401','403','405','407','409','411','413','415','419','421','423','425')
and cfw.business_type in ('101','103')
and cfw.business_type ='202'
and cfw.business_type in (select cfw.business_type from t_cust_fund_water cfw)
and cfw.business_type in ('502','503','504','506','508','510','512','514','516','518','520','521')
order by cfw.happen_time

select sum(if(CONVERT(cfw.business_type,SIGNED)%2=0,cfw.fund_value,0)) AS "exPenditure", sum(if(CONVERT(cfw.business_type,SIGNED)%2=1,cfw.fund_value,0)) AS "fundValue" from t_cust_fund_water cfw, t_cust_customer tcc WHERE cfw.customer_id = tcc.PID AND cfw.customer_id = #{custFundWater.customerId,jdbcType=VARCHAR}
and cfw.happen_time >= #{custFundWater.happenBeginTime,jdbcType=TIMESTAMP}
and cfw.happen_time <= #{custFundWater.happenEndTime,jdbcType=TIMESTAMP}
and cfw.business_type ='302'
and cfw.business_type in ('401','403','405','407','409','411','413','415','419','421','423','425')
and cfw.business_type in ('101','103')
and cfw.business_type ='202'
and cfw.business_type in (select cfw.business_type from t_cust_fund_water cfw)
and cfw.business_type in ('502','503','504','506','508','510','512','514','516','518','520','521')
order by cfw.happen_time

select cfw.PID, cfw.happen_time happen_time, cfw.account_balance account_balance, (SELECT ts.dict_cont_name FROM t_sys_dictionary tsd,t_sys_dictionary_content ts WHERE tsd.PID = ts.dict_id AND tsd.dict_code = 'CUST_TRADE_TYPE' AND ts.dict_cont_code = cfw.business_type) business_type, cfw.business_type business_type_value, cfw.fund_value fund_value, cfw.fun_wat_desc fun_wat_desc from t_cust_fund_water cfw,t_cust_customer tcc WHERE cfw.customer_id = tcc.PID AND cfw.customer_id = #{map.userId,jdbcType=VARCHAR}
and cfw.happen_time >= #{map.beginTime,jdbcType=TIMESTAMP}
and cfw.happen_time <= #{map.endTime,jdbcType=TIMESTAMP}
and cfw.business_type in ('302')
and cfw.business_type in ('401','403','405','407','409','411','413','415','419','421','423','425')
and cfw.business_type in ('101','103')
and cfw.business_type in ('202')
