mapper.select:
select 

from t_sys_menu m
where PID = #{pid,jdbcType=VARCHAR}

select DISTINCT m.PID, m.parent_auth_id, m.menu_name, m.menu_visible, m.menu_url, m.menu_desc, m.menu_order, m.menu_level, m.menu_name_en, m.is_leaf, m.menu_ico, m.menu_code from t_sys_menu m, t_sys_role_menu_butt_rel rmb, t_sys_user_role_rel urr where menu_visible = 1
and PID = #{pid,jdbcType=VARCHAR}
and parent_auth_id = #{parentAuthId,jdbcType=VARCHAR}
and menu_level = #{menuLevel,jdbcType=INTEGER}
and m.PID = rmb.menu_id and urr.role_id = rmb.role_id and urr.user_id = #{userId,jdbcType=VARCHAR} order by m.menu_level, m.menu_order
