mapper.select:
		select 

		from t_sys_vipinfo  sv
		where status != -1 

      	and  vip_code like concat('%',#{sysVipinfo.vipCode,jdbcType=VARCHAR},'%' )


      	and  vip_name like concat('%',#{sysVipinfo.vipName,jdbcType=VARCHAR},'%' )


      	and  vip_level = #{sysVipinfo.vipLevel,jdbcType=VARCHAR} 


      	and  start_time  >=  #{sysVipinfo.beginApplyStartData,jdbcType=TIMESTAMP}


      	and  start_time  <=  #{sysVipinfo.endApplyStartData,jdbcType=TIMESTAMP}


      	and  end_time  >=  #{sysVipinfo.beginApplyFinishData,jdbcType=TIMESTAMP}


      	and  end_time  <=  #{sysVipinfo.endApplyFinishData,jdbcType=TIMESTAMP}


      	and  status = #{sysVipinfo.status,jdbcType=VARCHAR}

select 

from t_sys_vipinfo sv
where sv.PID = #{pid,jdbcType=VARCHAR}
